# Author：Mike# date：2019/11/29""""""import sysimport pygamefrom settings import Settingsfrom ship import Ship# import game_functions as gffrom pygame.sprite import Groupclass AlienInvasion:    """全局类管理游戏的全部资产和操作行为"""    def __init__(self):        """初始化游戏,创建游戏的资源"""        pygame.init()  # 初始化游戏        self.settings = Settings()  # 创建一个设置对象获取设置模块的游戏参数        # 设置游戏框大小,用pygame.display.set_mode方法设置游戏的窗口大小        self.screen=pygame.display.set_mode((self.settings.screen_width,self.settings.screen_height))        # self.settings.screen_width = self.screen.get_rect().width        # self.settings.screen_height = self.screen.get_rect().height        pygame.display.set_caption(self.settings.game_name)        # 创建一艘飞船对象        self.ship = Ship(self)    def run_game(self):        """开始游戏主循环"""        while True:            # 监视键盘和鼠标的操作事件            self._check_events()            self.ship.update()            self._update_screen()    def _check_events(self):        """回应键盘或鼠标的事件"""        for event in pygame.event.get():            if event.type == pygame.QUIT:                sys.exit()            elif event.type == pygame.KEYDOWN:                self._check_keydown_events(event)            elif event.type == pygame.KEYUP:                self._check_keyup_events(event)    def _check_keydown_events(self,event):        """响应键盘按下的操作"""        if event.key == pygame.K_RIGHT:            self.ship.moving_right=True        elif event.key == pygame.K_LEFT:            self.ship.moving_left=True        elif event.key == pygame.K_q:            sys.exit()    def _check_keyup_events(self,event):        """响应键盘松开的操作"""        if event.key == pygame.K_RIGHT:            self.ship.moving_right = False        elif event.key == pygame.K_LEFT:            self.ship.moving_left = False    def _update_screen(self):        """更新图片在窗口的位置,重绘窗口"""        self.screen.fill(self.settings.bg_color)        self.ship.blitme()        pygame.display.flip()if __name__ == '__main__':    # 创建游戏实例,运行游戏    ai=AlienInvasion()    ai.run_game()