# Author：Mike# date：2019/11/29import pygameclass Ship():    """管理飞船ship的类"""    def __init__(self, ai_game):        """初始化ship并设置ship的初始位置"""        self.screen = ai_game.screen        self.settings=ai_game.settings        self.screen_rect = ai_game.screen.get_rect()        # 加载飞船图像并获取外接矩形        self.image = pygame.image.load('images/ship.bmp')  # 加载飞船图片        self.rect = self.image.get_rect()  # 获取飞船外接矩形坐标        # self.screen_rect = screen.get_rect()  # 获取游戏窗口坐标        # # 将每艘飞船放在屏幕中央底部        self.rect.midbottom = self.screen_rect.midbottom  # 飞船的矩形坐标位置放在窗口的坐标轴中间        #存储一个小数,为了记录飞船的位置        self.x = float(self.rect.x)        # 移动标志        self.moving_right = False        self.moving_left = False        #        # # 在飞船的属性Center中存储小数值        # self.center =    def update(self):        """根据移动标志调整飞船位置"""        # 更新飞船的X值,而不是rect        if self.moving_right and self.rect.right < self.screen_rect.right:  # 键盘的向右按钮按下,且飞船位置没有超过右边的边框位置            self.x += self.settings.ship_speed  # 飞船位置以设置速度向右移动        if self.moving_left and self.rect.left > 0:  # 键盘的向左按钮按下,且飞船位置没有超过左边的边框位置(0)            self.x -= self.settings.ship_speed  # 飞船位置以设置速度向左移动        # 根据self.center 更新rect对象        self.rect.x = self.x    def blitme(self):        """在指定位置绘制飞船"""        self.screen.blit(self.image, self.rect)  # 获取飞船图片,获取窗口位置绘制飞船